package com.example.demoforappstat;import android.content.Context;import android.util.Log;import android.widget.Toast;public class AppStatManager {		int state;	long time1;	Context mContext;	final String TAG_LAST_APP_MARK_TIME = "TAG_LAST_APP_MARK_TIME";	final String TAG_APP_STAT_USED_TIME = "TAG_APP_STAT_USED_TIME";	final String TAG = "AppStatManager";	final int STATE_START = 1;	final int STATE_NORMAL = 0;		final long ADAY = 24 * 60 * 60 * 1000;		public AppStatManager(Context context)	{		mContext = context;		state = STATE_NORMAL;	}		public void startStat()	{		if(state == STATE_NORMAL)		{			state = STATE_START;			time1 = System.currentTimeMillis();			}	}		public void stopStat()	{		long time2 = System.currentTimeMillis();				long statTime = time2 - time1;				  		addTime(time2, statTime);				state = STATE_NORMAL;	}		protected void addTime(long currentTime , long statTime)	{		long lastTimeMarked = ZakerShareByFile.getLong(mContext, TAG_LAST_APP_MARK_TIME, 0);				long lastUsedTime = ZakerShareByFile.getLong(mContext, TAG_APP_STAT_USED_TIME, 0);				long dTime = currentTime - lastTimeMarked;				if(dTime >= ADAY)		{			Log.e(TAG, "Last usedTime : " + statTime);			Toast.makeText(mContext, "Last usedTime : " + statTime, Toast.LENGTH_LONG).show();			ZakerShareByFile.saveLong(mContext, TAG_APP_STAT_USED_TIME, statTime);		}		else		{			Log.e(TAG, "Last usedTime : " + statTime);			lastUsedTime += statTime;			ZakerShareByFile.saveLong(mContext, TAG_APP_STAT_USED_TIME, statTime);			Toast.makeText(mContext, "Last usedTime Add : " + lastUsedTime, Toast.LENGTH_LONG).show();		}				ZakerShareByFile.saveLong(mContext, TAG_LAST_APP_MARK_TIME, currentTime);			}}